import { useQuery } from '@apollo/client';
import gql from 'graphql-tag';
import ErrorMessage from '../components/ErrorMessage';
import OrderItemStyles from '../components/styles/OrderStyles';
import Head from 'next/head';
import formatMoney from '../lib/formatMoney';
import styled from 'styled-components';
import Link from 'next/link';

const USER_ORDERS_QUERY = gql`
    query USER_ORDERS_QUERY{
        allOrders {
            id
            charge
            total
            user {
                id
            }
            items {
                id
                name
                description
                price
                quantity
                photo {
                    image {
                        publicUrlTransformed
                    }
                }
            }
        }
    }
`;
 const OrderUl = styled.ul`
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(350px, 1fr));
    grid-gap: 4rem;

 `
 function countItemsInOrder(order){
     return order.items.reduce((tally, item) => tally + item.quantity, 0);
 }
export default function OrdersPage(){
    const {data, error, loading} = useQuery(USER_ORDERS_QUERY);
    if(loading) return <p>Loading....</p>;
    if (error) return <ErrorMessage error={error} /> ;
    const {allOrders} = data;
    return (
        <>
        <Head><title>Sick Fits - Your ({allOrders.length}) Orders</title></Head>
      <h2>You have {allOrders.length} orders! </h2>
        <OrderUl>
            {allOrders.map(order => <OrderItemStyles>
                <Link href={`/order/${order.id}`}>
                    <>
                    <div className="order-meta">
                        <p>{countItemsInOrder(order)} Items</p>
                        <p>Product{order.items.length === 1 ? '' : 's'} {order.items.length}</p>
                        <p>{formatMoney(order.total)}</p>
                    </div>
                    <div className="images">
                        {order.items.map(item =>
                        ( <img key={`image-${item.id}`}
                        src={item.photo?.image?.publicUrlTransformed}
                        alt={item.name} />
                        ))}
                    </div>
                    </>
                </Link>
            </OrderItemStyles>)}


            </OrderUl>
            </>
    )
}
